import { Route, Routes, useParams, Link } from "react-router";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
  {
    id: "q1",
    author: "Bhautik",
    text: "Learning React is Fun!",
  },
  {
    id: "q2",
    author: "Bhautik Jani",
    text: "Learning React is Awesome!",
  },
  {
    id: "q3",
    author: "Bhautik Ashvinbhai Jani",
    text: "Learning React is Great!",
  },
];

const QuoteDetails = () => {
  const params = useParams();
  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId);

  if (!quote) {
    return (
      <h2 className="centered" style={{ color: "red" }}>
        No Quote Found with this ID!!!
      </h2>
    );
  }

  return (
    <>
      {/* <h1>Welcome to QuoteDetails</h1>
      <h2>{params.quoteId}</h2> */}

      <HighlightedQuote author={quote.author} text={quote.text} />

      <Routes>
        <Link to="/comments"> Comment</Link>
        <Route path="/comments" element={<Comments />}></Route>
      </Routes>
    </>
  );
};
export default QuoteDetails;
